declare global {
	const cds : import ('@sap/cds/apis/core')
		& import ('@sap/cds/apis/models')
		& import ('@sap/cds/apis/connect')
		& import ('@sap/cds/apis/serve')
		& import ('@sap/cds/apis/ql').cds_ql
		& import ('@sap/cds/apis/services').cds
		& import ('@sap/cds/apis/services').QueryAPI
	class SELECT<T> extends cds.ql.SELECT<T>{}
	class INSERT<T> extends cds.ql.INSERT<T>{}
	class UPDATE<T> extends cds.ql.UPDATE<T>{}
	class DELETE<T> extends cds.ql.DELETE<T>{}
	class CREATE<T> extends cds.ql.CREATE<T>{}
	class DROP<T> extends cds.ql.DROP<T>{}
}

export = cds
